import { useState } from "react";
import { NavLink, useParams } from "react-router-dom";

function Pagination(props) {
  let { page } = useParams();
  const [currentPage, setCurrentPage] = useState(page ? Number(page) - 1 : 0);

  if (props.data.posts.length) {
    const pages = [];
    for (let i=0; i < props.data.posts.length; i += 3) {
      pages.push(props.data.posts.slice(i, i+3))
    }

    const handlePreviousClick = () => {
      if(currentPage > 0) {
        setCurrentPage(currentPage - 1)
      }
    }

    const handleNextClick = () => {
      if(currentPage < pages.length - 1) {
        setCurrentPage(currentPage + 1)
      }
    }

    const page = pages[currentPage];
    const postsDisplayed = []

    page.forEach((post) => {
      postsDisplayed.push (
        <NavLink to={'/post/' + post.slug} key={post.slug}>
          <div className="recent-post">
            <img src={post.coverImage.url} alt='Post cover' />
            <span><p>{post.author.name},</p><p>{post.date}</p></span>
            <span><h3>{post.title}</h3></span>
          </div>
        </NavLink>
    )})

    return (
      <main>
        <div className="all-posts-container">
          {postsDisplayed}
          <span>
            {currentPage === 0 ? '' : (
              <NavLink to={`/page/${currentPage}`}>
                ← Previous
              </NavLink>
            )}
            {currentPage === pages.length - 1 ? '' : (
              <NavLink to={`/page/${currentPage + 2}`}>
                Next →
              </NavLink>
            )}
          </span>
        </div>
      </main>
    )
  }
}

export default Pagination;
